{
    "description": "Notify me of all GPS changes to show FlightRoute",
    "subject": {
        "entities": [
            {
                "idPattern": ".*",
                "type": "GPS"
            }
        ],
        "condition": {
            "attrs": [
                "latitude","longitude"
            ]
        }
    },
    "notification": {
        "http": {
            "url": "{{host}}:{{port}}/maphub"
        }
    }
}

