{
    "description": "Notify me of low number of sensors equiped in Drone 001",
    "subject": {
        "entities": [
            {
                "idPattern": ".*",
                "type": "InventoryItem"
            }
        ],
        "condition": {
            "attrs": [
                "numSensorsInstalled"
            ],
            "expression": {
                "q": "numSensorsInstalled<4;refDrone==urn:ngsi-ld:Drone:001"
            }
        }
    },
    "notification": {
        "http": {
            "url": "http://localhost:3000/subscription/low-sensors-drone001"
        },
        "attrsFormat": "keyValues"
    }
}
